import React from "react";
import {useState} from 'react';
import * as THREE from "three";
import Header from "./components/Header";
import Button from "./components/Button"
import Registration from "./components/Registration";
import Loading from "./components/Loading";
import Rendering from "./components/Rendering";

import { useThree} from "@react-three/fiber"
import { OBJLoader } from "three/examples/jsm/loaders/OBJLoader";


const checkuserurl = "http://localhost:5000/api/checkuser/";
const getobjfileurl = "http://localhost:5000/api/getfile/";

function ObjToPrimitive({ url, mat }) {
  const [obj, setObj] = useState();
  useMemo(() => new OBJLoader().load(url, setObj), [url]);
  if (obj) {
    obj.traverse((child) => {
      if (child instanceof Mesh) {
        child.material = mat;
      }
    });
    return <primitive object={obj} />;
  }
  return null;
}

function App() {
  var file;
  var preload;
  const mat = new THREE.MeshPhongMaterial()
  const [pageState, setState] = useState(0)
  const [nextSeqId, setNextSeqId] = useState(0)
  const [userId, setUserId] = useState('')
  const [filename, setFilename] = useState('')
  const handleLogin = (userName) => {
    console.log(userName)
    let xhttp = new XMLHttpRequest();
    xhttp.open("GET", checkuserurl+userName, true);
    xhttp.addEventListener('load', () => {
        console.log('Received an HTTP response.');
        var jsonresp = JSON.parse(xhttp.response);
        console.log(jsonresp);
        setNextSeqId(parseInt(jsonresp.nextId));
        setFilename(jsonresp.fileName)
        setState(1)
    });
    xhttp.send();
    setUserId(userName)
  }
  const load = () => {
    const url = getobjfileurl+filename;
    preload = ObjToPrimitive({ url: url, mat })
    setState(2)
  }
  const onClick = () =>{
    setState(3)
  }
  let page;
  if(pageState === 0){
    page = <Registration onRegister={handleLogin}/>;
  } else if (pageState === 1){
    page = <Loading onload={load}/>;
  } else if (pageState === 2){
    page = 
    <div>
      <Rendering preload={preload}/>
      {/* <Rendering url={getobjfileurl+filename}/> */}
      <Button text="model1" onClick={onClick}/>
      {/* <Rendering url={getobjfileurl+filename}/>
      <Rendering url={getobjfileurl+filename}/>
      <Button text="model2" onClick={onClick}/> */}
    </div>;
    //page = <Rendering file={file}/>;
  } else if (pageState === 3){
    page = <h1>Thanks!</h1>
  }
  var camera = new THREE.PerspectiveCamera(75, 1, 0.1, 1000);
  return (
    <div className="container">
      {page}
    </div>
  );
}

export default App;
