import React from "react";
import {useState} from 'react';
import * as THREE from "three";
import Header from "./components/Header";
import Registration from "./components/Registration";
import Loading from "./components/Loading";
import { useLoader } from 'react-three-fiber';
import { OBJLoader } from "three/examples/jsm/loaders/OBJLoader";
import { MTLLoader } from "three/examples/jsm/loaders/MTLLoader";

const checkuserurl = "http://localhost:5000/api/checkuser/";
const getobjfileurl = "http://localhost:5000/api/getfile/";

function App() {
  var file;
  let obj;
  const [pageState, setState] = useState(0)
  const [nextSeqId, setNextSeqId] = useState(0)
  const [userId, setUserId] = useState('')
  const [filename, setFilename] = useState('')
  const handleLogin = (userName) => {
    console.log(userName)
    let xhttp = new XMLHttpRequest();
    xhttp.open("GET", checkuserurl+userName, true);
    xhttp.addEventListener('load', () => {
        console.log('Received an HTTP response.');
        var jsonresp = JSON.parse(xhttp.response);
        console.log(jsonresp);
        setNextSeqId(parseInt(jsonresp.nextId));
        setFilename(jsonresp.fileName)
        setState(1)
    });
    xhttp.send();
    setUserId(userName)
  }
  const LoadObjFile = () => {
    obj = useLoader(OBJLoader, getobjfileurl+filename);
    console.log(obj)
    setState(2)

    return <h1>loading...</h1>
  }
  let page;
  if(pageState === 0){
    page = <Registration onRegister={handleLogin}/>;
  } else if (pageState === 1){
    page = <Loading onload={LoadObjFile}/>;
  } else if (pageState === 2){
    page = <h1>good</h1>;
  }
  var camera = new THREE.PerspectiveCamera(75, 1, 0.1, 1000);
  return (
    <div className="container">
      {page}
    </div>
  );
} 

export default App;
