import React from "react";
import {useState} from 'react';
import * as THREE from "three";
import Header from "./components/Header";
import Registration from "./components/Registration";
import Loading from "./components/Loading";
import Rendering from "./components/Rendering";


const checkuserurl = "http://localhost:5000/api/checkuser/";
const getobjfileurl = "http://localhost:5000/api/getfile/";

function App() {
  var file;
  const [pageState, setState] = useState(0)
  const [nextSeqId, setNextSeqId] = useState(0)
  const [userId, setUserId] = useState('')
  const [filename, setFilename] = useState('')
  const handleLogin = (userName) => {
    console.log(userName)
    let xhttp = new XMLHttpRequest();
    xhttp.open("GET", checkuserurl+userName, true);
    xhttp.addEventListener('load', () => {
        console.log('Received an HTTP response.');
        var jsonresp = JSON.parse(xhttp.response);
        console.log(jsonresp);
        setNextSeqId(parseInt(jsonresp.nextId));
        setFilename(jsonresp.fileName)
        setState(2)
    });
    xhttp.send();
    setUserId(userName)
  }
  const load = () => {
    let xhttp = new XMLHttpRequest();
    xhttp.open("GET", getobjfileurl+filename, true);
    xhttp.addEventListener('load', () => {
        console.log('Received an HTTP response.');
        var resp = xhttp.response;
        file = resp;
        console.log(typeof(file));
        setState(2)
    });
    xhttp.send();
  }
  let page;
  if(pageState === 0){
    page = <Registration onRegister={handleLogin}/>;
  } else if (pageState === 1){
    page = <Loading onload={load}/>;
  } else if (pageState === 2){
    page = <Rendering url={getobjfileurl+filename}/>;
    //page = <Rendering file={file}/>;
  }
  var camera = new THREE.PerspectiveCamera(75, 1, 0.1, 1000);
  return (
    <div className="container">
      {page}
    </div>
  );
}

export default App;
