import * as THREE from "three";
import { Canvas, useFrame, useRender, useThree } from "@react-three/fiber"
import {apply} from "react-three/fiber"
import { OBJLoader } from "three/examples/jsm/loaders/OBJLoader";
import React, { useMemo, useState } from "react";
import { Mesh } from "three";
import { OrbitControls } from "three/examples/jsm/controls/OrbitControls";



apply({ OrbitControls })

function Controls(props) {
  const ref = useRef()
  const { camera } = useThree()
  useRender(() => ref.current.obj.update())
  return <orbitControls ref={ref} args={[camera]} {...props} />
}

function ObjToPrimitive({ url, mat }) {
    const [obj, setObj] = useState();
    useMemo(() => new OBJLoader().load(url, setObj), [url]);
    if (obj) {
      obj.traverse((child) => {
        if (child instanceof Mesh) {
          child.material = mat;
        }
      });
      return <primitive object={obj} />;
    }
    return null;
}

const Rendering = ({url}) => {
    const mat = new THREE.MeshPhongMaterial()
    
    return (
        <div style={{width: 300, height:300}}>
            <Canvas camera={{position:[0,0,1], fov:60}}>
                <Controls/>
                <mesh position={[0,0,0]}>
                    {ObjToPrimitive({ url: url, mat })}
                </mesh>
            </Canvas>
        </div>
    )
}

export default Rendering
