import React from "react";
import {useState} from 'react';
import * as THREE from "three";
import Header from "./components/Header";
import Registration from "./components/Registration";
import Loading from "./components/Loading";
const back_end_url = "localhost:5000/api/checkuser/";

function App() {
  const [pageState, setState] = useState(0)
  const [nextSeqId, setNextSeqId] = useState(0)
  const [userId, setUserId] = useState('')
  const handleLogin = (userName) => {
    console.log(userName)
    let xhttp = new XMLHttpRequest();
    xhttp.open("GET", back_end_url+userName, true);
    xhttp.addEventListener('load', () => {
        console.log('Received an HTTP response.');
        var jsonresp = xhttp.response;
        console.log(jsonresp);
        setNextSeqId(parseInt(jsonresp.nextId));
        setState(1)
    });
    xhttp.send();
    setUserId(userName)
  }
  let page;
  if(pageState === 0){
    page = <Registration onRegister={handleLogin}/>;
  } else if (pageState === 1){
    page = <Loading/>;
  }
  var camera = new THREE.PerspectiveCamera(75, 1, 0.1, 1000);
  return (
    <div className="container">
      {page}
    </div>
  );
}

export default App;
